(19, 0, 0, '', '', '', '', '', 'Provides a modal with rows of formfields that you specify. As many options can be added as desired. Note this form field has a jQuery based javascript file as a dependency.', '05bf68d4-52f9-4705-8ae7-cba137fce0ad', 0, '', 'Repeatable', '', '{"properties0":{"name":"type","example":"repeatable","adjustable":"1","description":"(mandatory) must be repeatable."},"properties1":{"name":"name","example":"repeatable_list","adjustable":"1","description":"(mandatory) is the unique name of the parameter"},"properties2":{"name":"label","example":"The Repeatable List","adjustable":"1","translatable":"1","description":"(mandatory) (translatable) is the descriptive title of the field."},"properties3":{"name":"description","example":"","adjustable":"1","translatable":"1","description":"(optional) (translatable) description text for the form field. Displays at the top of the modal with the name as well as in the usual position in the form"},"properties4":{"name":"default","example":"","adjustable":"1","description":"(optional) The default value for the form field if the field is left empty. Note this has to be a json string compatible with the contents of the form field."},"properties5":{"name":"id","example":"aid","adjustable":"1","mandatory":"1","description":"(mandatory) id of the hidden from field. (the modal will have this id with an added suffix of \\"_modal\\" and the table within the modal will have this id with a suffix of _modal_table\\")"},"properties6":{"name":"class","example":"","adjustable":"1","description":"(optional) class of the table."},"properties7":{"name":"select","example":"Click here","adjustable":"1","translatable":"1","description":"(optional) (translatable) The text to show on the modal button."},"properties8":{"name":"icon","example":"list","adjustable":"1","description":"(optional) The icon to show on the select button (is prefixed with \\"icon-\\")."},"properties9":{"name":"maximum","example":"50","adjustable":"1","descri